package primerospasos;

public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a=5;
		
		double b;
		
		b=7;
		
		double c=b+a;
		System.out.println("LA SUMA DE C ES : "+c);
		c+=6;
		System.out.println("LA SUMA DE C ES : "+c);

		//en tipo int
		 c =b/a;
		System.out.println("LA DIVISION DE C ES : "+c);
		
		//en float
		double c2=b/a;
		System.out.println("LA DIVISION DE C ES : "+c2);

		

		 
		
		
		
		
		

	}

}
