package primerospasos;

public class manipulaciondecadenas3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String alumno1, alumno2;
		
		alumno1="David";
		alumno2="david";
		
		System.out.println(alumno1.equals(alumno2));//equals dice true false si es igual pero si diferencia mayusculas y minusculas
		
		System.out.println(alumno1.equalsIgnoreCase(alumno2));//equals dice true false si es igual con IgnoreCase se ignora mayuscula y minusculas
		
		
		
		
	
	}

}
